<div class="row">
    <div class="col-lg-6">
        <!--begin::Portlet-->
        <div class="m-portlet">
            <div class="m-portlet__head">
                <div class="m-portlet__head-caption">
                    <div class="m-portlet__head-title">
                        <h3 class="m-portlet__head-text">
                            Loaders
                        </h3>
                    </div>
                </div>
            </div>
            <div class="m-portlet__body">
                <table class="table table-bordered">
                    <tr>
                        <td style="vertical-align:middle; width: 30%">Basic loader</td>
                        <td style="vertical-align:middle; text-align: left;">
                            <div class="m-loader" style="width: 30px; display: inline-block;"></div>       
                        </td>
                    </tr>
                    <tr>
                        <td style="vertical-align:middle; width: 30%">Loader states</td>
                        <td style="vertical-align:middle; text-align: left;">
                            <div class="m-loader m-loader--brand" style="width: 30px; display: inline-block;"></div>
                            <div class="m-loader m-loader--primary" style="width: 30px; display: inline-block;"></div>
                            <div class="m-loader m-loader--success" style="width: 30px; display: inline-block;"></div>       
                            <div class="m-loader m-loader--info" style="width: 30px; display: inline-block;"></div>
                            <div class="m-loader m-loader--warning" style="width: 30px; display: inline-block;"></div>
                            <div class="m-loader m-loader--danger" style="width: 30px; display: inline-block;"></div>       
                        </td>
                    </tr>
                    <tr>
                        <td style="vertical-align:middle; width: 30%">Loader sizing</td>
                        <td style="vertical-align:middle; text-align: left;">
                            <div class="m-loader m-loader--sm" style="width: 30px; display: inline-block;"></div>
                            <div class="m-loader" style="width: 30px; display: inline-block;"></div>
                            <div class="m-loader m-loader--lg" style="width: 30px; display: inline-block;"></div>       
                        </td>
                    </tr>
                    <tr>
                        <td style="vertical-align:middle; width: 30%">Using on inputs</td>
                        <td style="vertical-align:middle; text-align: left;">
                            <div class="m-loader m-loader--primary m-loader--right">
                                <input type="text" class="form-control"/>
                            </div>
                            <div class="m--space-10"></div>
                            <div class="m-loader m-loader--brand m-loader--left">
                                <input type="text" class="form-control"/>
                            </div>    
                        </td>
                    </tr>
                    <tr>
                        <td style="vertical-align:middle; width: 30%">Using on buttons</td>
                        <td style="vertical-align:middle; text-align: left;">
                            <a href="#" class="btn m-btn btn-success m-loader m-loader--light m-loader--right">
                                Button
                            </a>
                            <a href="#" class="btn m-btn btn-primary m-loader m-loader--light m-loader--left">
                                Button
                            </a>
                            <a href="#" class="btn m-btn btn-danger m-loader m-loader--light m-loader--right">
                                Button
                            </a>
                            <div class="m-separator m-separator--dashed"></div>
                            <a href="#" class="btn m-btn btn-outline-success m-loader m-loader--success m-loader--right">
                                Button
                            </a>
                            <a href="#" class="btn m-btn btn-outline-primary m-loader m-loader--primary m-loader--left">
                                Button
                            </a>
                            <a href="#" class="btn m-btn btn-outline-danger m-loader m-loader--danger m-loader--right">
                                Button
                            </a>
                            <div class="m-separator m-separator--dashed"></div>
                            <a href="#" class="btn m-btn btn-success m-btn--custom m-loader m-loader--light m-loader--right">
                                Button
                            </a>
                            <a href="#" class="btn m-btn btn-primary m-btn--custom m-loader m-loader--light m-loader--left">
                                Button
                            </a>
                            <a href="#" class="btn m-btn btn-danger m-btn--custom m-loader m-loader--light m-loader--right">
                                Button
                            </a>
                            <div class="m-separator m-separator--dashed"></div>
                            <a href="#" class="btn m-btn btn-outline-success m-btn--custom m-loader m-loader--success m-loader--right">
                                Button
                            </a>
                            <a href="#" class="btn m-btn btn-outline-primary m-btn--custom m-loader m-loader--primary m-loader--left">
                                Button
                            </a>
                            <a href="#" class="btn m-btn btn-outline-danger m-btn--custom m-loader m-loader--danger m-loader--right">
                                Button
                            </a>
                        </td>
                    </tr>
                </table>
            </div>
        </div>
        <!--end::Portlet-->
    </div>
    
    <div class="col-lg-6">
        <!--begin::Portlet-->
        <div class="m-portlet">
        	<div class="m-portlet__head">
        		<div class="m-portlet__head-caption">
        			<div class="m-portlet__head-title">
        				<h3 class="m-portlet__head-text">
        					Default Spinner
        				</h3>
        			</div>
        		</div>
        	</div>
        	<div class="m-portlet__body">
        		<table class="table table-bordered">
                    <tr>
                        <td style="vertical-align:middle; width: 30%">Basic spinner</td>
                        <td style="vertical-align:middle;">
                            <div class="m-spinner"></div>       
                        </td>
                    </tr>
                    <tr>
                        <td style="vertical-align:middle; width: 30%">Spinner states</td>
                        <td style="vertical-align:middle;">
                            <div class="m-spinner m-spinner--brand"></div>
                            <div class="m-spinner m-spinner--primary"></div>
                            <div class="m-spinner m-spinner--success"></div>       
                            <div class="m-spinner m-spinner--info"></div>
                            <div class="m-spinner m-spinner--warning"></div>
                            <div class="m-spinner m-spinner--danger"></div>       
                        </td>
                    </tr>
                    <tr>
                        <td style="vertical-align:middle; width: 30%">Small Size</td>
                        <td style="vertical-align:middle;">
                            <div class="m-spinner m-spinner--brand m-spinner--sm"></div>
                            <div class="m-spinner m-spinner--primary m-spinner--sm"></div>
                            <div class="m-spinner m-spinner--success m-spinner--sm"></div>       
                            <div class="m-spinner m-spinner--info m-spinner--sm"></div>
                            <div class="m-spinner m-spinner--warning m-spinner--sm"></div>
                            <div class="m-spinner m-spinner--danger m-spinner--sm"></div>          
                        </td>
                    </tr>
                    <tr>
                        <td style="vertical-align:middle; width: 30%">Large Size</td>
                        <td style="vertical-align:middle;">
                            <div class="m-spinner m-spinner--brand m-spinner--lg"></div>
                            <div class="m-spinner m-spinner--primary m-spinner--lg"></div>
                            <div class="m-spinner m-spinner--success m-spinner--lg"></div>       
                            <div class="m-spinner m-spinner--info m-spinner--lg"></div>
                            <div class="m-spinner m-spinner--warning m-spinner--lg"></div>
                            <div class="m-spinner m-spinner--danger m-spinner--lg"></div>   
                        </td>
                    </tr>
                </table>
        	</div>
        </div>
        <!--end::Portlet-->
    </div>
</div>